/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    private int result = 0;

    public int pseudoPalindromicPaths (TreeNode root) {
        this.dfs(new int[9], root);

        return this.result;
    }

    private void dfs(int[] counter, TreeNode node) {
        if (node == null)
            return;

        counter[node.val - 1]++;

        if (node.left == null && node.right == null && this.isPalindrome(counter))
            this.result++;

        this.dfs(counter, node.left);
        this.dfs(counter, node.right);

        counter[node.val - 1]--;
    }

    private boolean isPalindrome(int[] counter) {
        int odd = 0;
        for (int c: counter) {
            if (c % 2 == 1)
                odd++;
            if (odd > 1)
                return false;
        }

        return true;
    }
}
