class Solution {
    /*
        Time - O(N), N = number of nodes.
        Space - O(H), H = Height of binary tree.
    */
    int count = 0;

    public int pseudoPalindromicPaths (TreeNode root) {
        int[] freq = new int[10]; // Node values in the range 1 to 9.
        helper(root, freq); 
        return count;
    }

    void helper(TreeNode root, int[] freq) { // Calculate frequency of digits in each branch.
        if(root == null) { // Do nothing.
            return;
        }
        if(root.left == null && root.right == null) { // If leaf node, update frquency, check if valid palindrome can be formed, backstrack.
            freq[root.val] ++;
            if(validPerm(freq)) { // If valid palindrome can be formed, increment count.
                count ++;
            }
            freq[root.val] --;
            return;
        }
        // For interior nodes, update frquency, call for left and right child, backstrack.
        freq[root.val] ++; 
        helper(root.left, freq);
        helper(root.right, freq);
        freq[root.val] --;
    }

    boolean validPerm(int[] freq) { // We can have a valid palindrome only if the frequency of atmost 1 digit is odd.
        int odd = 0;
        for(int i = 1; i <= 9; i ++) {
            if(freq[i] % 2 == 1) {
                odd ++;
            }
        }
        return odd <= 1;
    }
}
