```C#
public class Solution {
    public int PseudoPalindromicPaths (TreeNode root) {
        if (root is null) return 0;

        var x = new Dictionary<int, int>();
        return Solve(root, x);
    }

    private int Solve(TreeNode root, Dictionary<int, int> x) {
        if (root is null) return 0;

        x.TryGetValue(root.val, out var repetitions);
        x[root.val] = repetitions + 1;

        int count;
        if (root.left is null && root.right is null) {
            if (IsPseudoPalindrome(x)) count = 1;
            else count = 0;
        }
        else {
            count = Solve(root.left, x) + Solve(root.right, x);
        }

        x[root.val] = repetitions;

        return count;
    }

    private bool IsPseudoPalindrome(Dictionary<int, int> x) {
        bool hadOdd = false;
        foreach (var kvp in x) {
            if (kvp.Value % 2 != 0) {
                if (hadOdd) return false;
                hadOdd = true;
            }
        }

        return true;
    }
}
