class Solution {
public:
    vector<vector<int>> paths;
    int ans=0;
    bool isleaf(TreeNode* n){
        return n->left==NULL and n->right==NULL;
    }
    void check(map<int,int>& mp,vector<int>& v){
        if(v.size()%2==0){
                int f=0,cto=0,cte=0;
                for(auto ele:mp){
                    if(ele.second%2!=0){
                     //odd occurence
                      f=1;
                      break;
                    }
                    else{
                        cte++;
                    }
                }
                if(!f){
                    ans++;
                }
            }
            else if(v.size()%2!=0){
                //all even occurence and only 1 odd occurence
                int f=0;
                int cto=0,cte=0;
                for(auto ele:mp){
                    if(ele.second%2!=0){
                        cto++;
                        if(cto>1){f=1;break;}
                    }
                }
                if(!f){
                    ans++;
                }
            }
    }
    void helper(TreeNode* root,vector<int>& v,map<int,int>& mp,int& cte,int& cto){
        if(root==NULL){
            return;
        }
        v.push_back(root->val);
        mp[root->val]++;
        if(isleaf(root)){
           check(mp,v);
        }
        helper(root->left,v,mp,cte,cto);
        helper(root->right,v,mp,cte,cto);
        mp[v.back()]--;

        v.pop_back();

    }
    int pseudoPalindromicPaths (TreeNode* root) {
        //we can store all paths from root node to leaves
        vector<int> v;
        map<int,int> mp;
        int cte=0,cto=0;
        helper(root,v,mp,cte,cto);
        return ans;
    }
};
