/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
#define node TreeNode
class Solution {
public:
    int ans;
    bool check(map<int,int> &mm){
        bool flag=true;
        for(auto &v: mm){
            if(v.second%2==0) continue;
            else if(flag) flag=false;
            else return false;
        }
        return true;
    }

    void rec(node *curr,map<int,int> &mm){
        if(!curr) return;

        if(curr and !curr->left and !curr->right){
            mm[curr->val]++;

            if(check(mm)) ans++;

            mm[curr->val]--;
            if(mm[curr->val]==0) mm.erase(curr->val);

            return;
        }

        mm[curr->val]++;
        rec(curr->left,mm);
        rec(curr->right,mm);
        mm[curr->val]--;
        if(mm[curr->val]==0) mm.erase(curr->val);

        return;
    }

    int pseudoPalindromicPaths (TreeNode* root) {
        ans=0;
        map<int,int> mm;
        rec(root,mm);

        return ans;
    }
};
