class Solution {

    public int pseudoPalindromicPaths (TreeNode root) {
        if(root==null)
            return 0;

        int[] freq = new int[10];                   //to store frequency of each element
        return solveItForMe(root,freq);         

    }

    private int solveItForMe(TreeNode root,int[] freq){

        if(root==null)
            return 0;

        freq[root.val]++;                           //increase count by 1 of node's value

        if(root.left==null && root.right==null){    //if node's left and right both are null, that is leaf node, Now check pseudo-palindrome is there or not
            int isPalin = 0;                        //initialize it by zero
            for(int i=1;i<=9;i++)                   //Given node's values are only from 1 to 9
                isPalin += freq[i]%2;               //add the modulo 2 of that elements frequency, to count number of odd frequency elements

            freq[root.val]--;                       //BACKTRACK, reduce that(leaf node) element's frequency by one
            if(isPalin==1 || isPalin==0)            //if isPalin>1 it means more than 1 odd frequency elements are present else return 1
               return 1;
            else
               return 0;  
        }

        int count = solveItForMe(root.left,freq) + solveItForMe(root.right,freq);  //count the no. of pseudo-palindromic paths going from the root node to leaf nodes.   
        freq[root.val]--;                            //BACKTRACK

        return count;

    }
}

