class Solution {
public:
    bool isValid(string s) 
	{
       stack<char>stk;
	   int idx = 0;

	   while(idx < s.length())
	   {
	       char ch = s[idx];

           if(ch == 'a')
           { 
           	   stk.push(ch);
		   }
		   else if(ch == 'b')
		   {
		   	   stk.push(ch);
		   }
		   else if(ch == 'c')
		   {
		   	    if(stk.size() >= 2)
		   	    {
		   	         char ch2 = stk.top();
		   	         stk.pop();
					 char ch1 = stk.top();	
					 stk.pop();

					 if(!(ch1=='a' and ch2=='b' and ch=='c'))
					 {
					 	 stk.push(ch1);
					 	 stk.push(ch2);
					 	 stk.push(ch);
					 }
		        } 
		   }
		   idx++;
	   }    
	   if(stk.empty()==true and idx>=s.length())
	   return true;

	   return false;
    }
};
```

