           Stack<Character> st = new Stack<>();

    for(int i=0;i<s.length();i++){

        char ch = s.charAt(i);

        if(ch == 'a' || ch == 'b'){
            st.push(ch);
        }
        else{

                if(st.size()>0 && st.peek() == 'b'){
                    st.pop();
