class Solution {
    public boolean isValid(String S) {
        Stack<Integer> stack = new Stack();

        for(int i = 0; i < S.length(); ++i){
            if(stack.isEmpty()){
                if(S.charAt(i) == 'a'){
                    stack.push(1);
                }
                else{
                    return false;
                }
            }
            else{
                switch(S.charAt(i)){
                    case 'a':
                        stack.push(1);
                        break;
                    case 'b':
                        if(stack.peek() == 1){
                            stack.pop();
                            stack.push(2);
                        }
                        else{
                            return false;
                        }
                        break;
                    default:
                        if(stack.peek() == 2){
                            stack.pop();
                        }
                        else{
                            return false;
                        }
                        break;
                }
            }
        }

        if(stack.isEmpty()){
            return true;
        }

        return false;
    }
}
