class Solution {
public:
    bool isValid(string s) {
        if(s.size()==0)
            return true;

        int n = s.length();
        int find = s.find("abc");
        if(find != string :: npos)
        {
            string t1 = s.substr(0, find); //1st part before "abc"
            string t2 = s.substr(find+3, n); //2nd part after "abc"

            return isValid(t1+t2); 
        }

        return false;
    }
};
```

