class Solution {
    public int[] shuffle(int[] nums, int n) {
        int [] ans = new int[nums.length];
        int index = 0;

        for(int i = 0; i < n; i++) {
            ans[index++] = nums[i];
            ans[index++] = nums[n + i];
        }
        return ans;
    }
}

class Solution {
    public int[] shuffle(int[] nums, int n) {
        int[] newArray = new int[2*n];  here we taking the length double of (n) given in our question
        int x = 0;
        int y = n;
        int z = 0;

        while(x < n) {
            newArray[z++] = nums[x++];
            newArray[z++] = nums[y++];
        } 
        return newArray;
    }
}

