class Solution
{
    public String removeOuterParentheses(String S) 
    {
        Stack<Character> valid=new Stack<>();//checking the balance and when the stack is Empty
        List<Integer> index=new ArrayList<>();//for storing the index
        char ch;

        for(int i=0;i<S.length();i++)//traversering the indices 
        {
            ch=S.charAt(i);//extracting charracter
            if(valid.isEmpty())//new valid parentheses 
            {
                valid.push(ch);//pushing it into the stack the open parentheses 
                index.add(i);//and pushing it corresponding index 
            }
            else if(ch==')'&&valid.peek()=='(')
            {
                valid.pop();
                if(valid.isEmpty())//ending of new valid parentheses
                   index.add(i);//storing the closing index 
            }
            else
            {
                valid.push(ch);//otherwise pushing he open parentheses 
            }
        }
        StringBuilder res=new StringBuilder();//resultant 
        for(int i=0;i<index.size();i+=2)//index is always in pair i.e;starting and ending 
        {
            res.append(S.substring(index.get(i)+1,index.get(i+1)));//removing the outmost parenthesis for the given pair of indices 
        }
        return res.toString();//returning the String 
    }
}//Please do vote me, It helps a lot
