class Solution {
public:
    string removeOuterParentheses(string S) {
        int flag = 0,l = int(S.length());
        string result;
        for(int i = 0;i < l;i++){
            if(S[i] == '('){
                flag += 1;
                result += flag > 1 ? "(" : "";
            } else {
                flag -= 1;
                result += flag > 0 ? ")" : "";
            }
        }
        return result;
    }
};

