var removeOuterParentheses = function(S) {
    let res = '',
        leftNum = 0;
    for(let i = 0; i < S.length; ++i) {
        if(S.charAt(i) == '(') {
            leftNum ++;
            if(leftNum == 2) {
                while(leftNum > 0) {
                    res += S.charAt(i);
                    S.charAt(++i) == '(' ? leftNum ++ : leftNum --;

                }
            }
        } else
            leftNum --;
    }
    return res;
};
