class Solution:
    def removeOuterParentheses(self, s: str) -> str:
        output = []
        count = 0
        for c in s:
            if c == '(':
                if count > 0: output.append(c)
                count += 1
            if c == ')':
                if count > 1: output.append(c)
                count -= 1
        return "".join(output)
