class Solution {
public:
    string removeOuterParentheses(string S) {
        int depth=0;
        string p;
        for(int i=0;i<S.length();i++){
            if (S[i]=='(') {
				if (depth!=0){
                    p+='(';
				}
                depth+=1;
			}
            if (S[i]==')'){
				if (depth-1!=0){
                    p+=')';
				}
                depth-=1;
			}    
        }
        return p;
    }
};

