    string removeOuterParentheses(string S) {
        stack<char>s;
        vector<int>partitions;
        for(int i=0;i<S.length();i++){
            if(S[i]=='(') s.push(S[i]);
            else {
                s.pop();
                if(s.empty()) partitions.push_back(i);
            }
        }
        int start = 1;
        string ans = "";
        for(int i=0;i<partitions.size();i++){
            string yo = S.substr(start,partitions[i]-start);
            ans += yo;
            start = partitions[i]+2;
        }
        return ans;
    }
};
