string removeOuterParentheses(string s) {
        int i=0,j=0,countOfBrackets=0;
        while(j<s.length())
        {
            if(s[j]=='(')
            {
                countOfBrackets++;
                j++;
            }else{
                countOfBrackets--;
                if(countOfBrackets==0)
                {
                    s[i]='0';
                    s[j]='0';
                    i=j+1;
                }
                j++;
            }
        }
        string ans="";
        for(int i=0;i<s.length();i++)
        {
            if(s.at(i)!='0')
                ans+=s[i];
        }
        return ans;
    }
