class Solution {
    public String removeOuterParentheses(String s) {

        StringBuilder res = new StringBuilder();

        int count=0;

        for(int i=0; i<s.length(); i++){

            char c=s.charAt(i);
            int prev=count;
            if(c=='(') count++;
            else count--;

            if(prev==0 && count==1) continue; // if it is a starting '(' of outer most parentheses then continue
            else if(prev==1 && count==0) continue; // if it is a ending ')' of the outer most parentheses then continue

            res.append(c); // else append it to result
        }

        return res.toString();

    }
}

