class Solution {
public:
    string removeOuterParentheses(string s) {
        int countLeft = 0, countRight = 0;
        string ans = "";
        stack<char> st;

        for(int i =0;i<s.size(); i++){
            if(s[i] == '('){
                countLeft++;
                if(countLeft > 1){
                    st.push(s[i]);
                }
            }else if(countLeft == countRight+1){
                countLeft = 0;
                countRight = 0;
                continue;
            }else if(s[i] == ')'){
                st.push(s[i]);
                countRight++;
            }
        }

        while(!st.empty()){
            ans += st.top();
            st.pop();
        }

        reverse(ans.begin(), ans.end());

        return ans;
    }
};
