 def runningSum(self, nums: List[int]) -> List[int]:

        ans = []

        memo = [None] * len(nums)
        memo[0] = nums[0]

        def cumsum(ind):
            if memo[ind] is None:

                memo[ind] = nums[ind] + cumsum(ind-1)

            return memo[ind]

        for i in range(len(nums)):
            ans.append(cumsum(i))

        return ans
