class Solution {
    func runningSum(_ nums: [Int]) -> [Int] {
        var res = [Int]()
        for i in 0..<nums.count where !nums.isEmpty {
            i == 0 ? res.append(nums[i]) : res.append(res[i-1] + nums[i])
        }
        return res
    }
}
```

