class Solution {
public:
    vector<int> runningSum(vector<int>& nums) {
        if(nums.size() <= 1) {
            return nums;
        }
        recur(nums, 1);
        return nums;
    }

    void recur(vector<int>& nums, int count) {
        if(count == nums.size()) {
            return;
        }
        nums.at(count) = nums.at(count) + nums.at(count - 1);
        recur(nums, count+1);
    }
};
```

