class Solution {
public:
    bool isRobotBounded(string instructions) {
        int counter = 0;        bool shift = true;
        pair<int, int> pos = {0, 0};        
        int x = 0, y = 1;
        for(int i = 0; i < instructions.size(); ++i){
            if(instructions[i] == 'L'){
                if(y == 1){
                    x = -1, y = 0;
                } else if(y == 0 && x == -1){
                    x = 0, y = -1;
                } else if(y == -1 && x == 0){
                    x = 1, y = 0;
                } else{
                    x = 0, y = 1;
                }
                ++counter;
            } else if(instructions[i] == 'R'){
                if(y == 1){
                    x = 1, y = 0;
                } else if(y == 0 && x == -1){
                    x = 0, y = 1;
                } else if(y == -1 && x == 0){
                    x = -1, y = 0;
                } else{
                    x = 0, y = -1;
                }
                --counter;
            } else{
                pos = {pos.first + x, pos.second + y};
                shift = true;
            }
        }
        counter = counter % 4;


        if(counter != 0 || !shift || (pos.first == 0 && pos.second == 0)){
            return true;
        }
        return false;
    }
};
