class Solution {
public:
    bool isRobotBounded(string instructions) {
        // 0: North,  1: East, 2: South, 3: West
        vector<vector<int>>movement={{0,1},{1,0},{0,-1},{-1,0}}; // Movements in x, y co-ordinate according to direction 
        int x=0,y=0,i=0;
        for(char c: instructions)
        {
            if(c=='R') i=(i+1)%4; // changing direction
            if(c=='L') i=(i+3)%4; // changing direction
            if(c=='G')
            {
                //  Moving according to current direction
                x+=movement[i][0];
                y+=movement[i][1];
            }
        }
        // In the end if we are at (0,0) then return true. Or if, we are at some other place and we are not facing North then we will reach at (0,0) either after 1 or 3 more steps. Hence return true.
        return  ((x==0 && y==0 ) || i!=0);
    }
};
```

