public class Solution {
    public bool IsRobotBounded(string instructions) {

        //**** Method 1: Without Modulo ****//
        //Visualizing Clock wise motion //

        int x = 0;
        int y = 0;
        int dx = 0;
        int dy = 1;

        foreach(var ch in instructions)
        {
            if(ch == 'G')
            {
                x += dx;
                y += dy;
            }
            else if(ch == 'R')
            {
                var temp = dx;
                dx = dy;
                dy = -temp;
            }
            else if(ch == 'L')
            {
                var temp = dy;
                dy = dx;
                dx = -temp;
            }
        }
        return (x == 0 && y == 0 || dy != 1);
    }
}
