class Solution {
    public boolean isRobotBounded(String instructions) {
        int direction = 0; // 0: north, 1: west, 2: south, 3: east
        int pos[] = new int[]{0,0}; //initial position X and Y co-ordinate
        for(char instruction: instructions.toCharArray()){
            if(instruction == 'L'){
                direction = ++direction%4; //direction always takes values in 0 to 3
            }else if(instruction == 'R'){
                direction--;
                if(direction<0){
                    direction = 3;
                }
            }else{
                if(direction==0){
                    pos[1]++; //increase y co-ordinate
                }else if(direction==1){
                    pos[0]--; //decrease x co-ordinate
                }else if(direction==2){
                    pos[1]--; //decrease y co-ordinate
                }else{
                    pos[0]++;  //increase x co-ordinate
                }
            }

        }

        return (pos[0]==0 && pos[1]==0)?true:(direction!=0); //return true if initial postion = final position and if direction has changed

    }
}

