class Solution {
    func isRobotBounded(_ instructions: String) -> Bool {
        var currentPosition: (x: Int, y: Int) = (0, 0)
        var currentDirection: Direction = .north

        guard !instructions.isEmpty else { return true }

        let instrutionsArray = instructions.map { String($0) }

        for instruction in instrutionsArray {
            switch instruction {
                case "G":
                    let coordinates = currentDirection.coordinates
                    currentPosition.x += coordinates.x
                    currentPosition.y += coordinates.y
                case "L", "R":
                    currentDirection = currentDirection.getNewDirection(instruction)
                default:
                    continue
            }
        }

        if (currentDirection != .north) || currentPosition == (0, 0) {
            return true
        }
        return false
    }
}

enum Direction {
    case north
    case west
    case east
    case south

    var coordinates: (x: Int, y: Int) {
        switch self {
            case .north:
                return (x: 0, y: 1)
            case .west:
                return (x: 1, y: 0)
            case .east:
                return (x: -1, y: 0)
            case .south:
                return (x: 0, y: -1)
        }
    }

    func getNewDirection(_ movement: String) -> Direction {
        switch self {
            case .south:
                return (movement == "L") ? .east : .west
            case .north:
                return (movement == "L") ? .west : .east
            case .west:
                return (movement == "L") ? .south : .north
            case .east:
                return (movement == "L") ? .north : .south
        }
    }
}
