    bool isRobotBounded(string s) {

        // We will store the possible direction after rotation 

        // Rotating North ('L' , 'R') , West , South , East

        // We will also store the value of each directions 'G' moving

        vector<pp> curr_path={{0 , 1} , {-1 , 0} , {0 , -1} , {1 , 0}};

                            // North , west     , south     , east

        vector<pp> rotation={ {1,3} , {2,0} , {3 , 1} , {0,2}};
          // First rotating north , west , south , east 

          // after rotation pair of values , first represents 'L' Rotation , second
          // 'R' rotation of direction

        // Rotation stores the index of the dirns which is currently after rotations

        // pair of value , in which first represents for Rotating left , Rotating right
        int x=0 , y=0;
        int dirn=0;

        // dirn = 0 (North ) , 1 (west) , 2 (south) , 3 (east)
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='G')
            {
                x+=curr_path[dirn].first;
                y+=curr_path[dirn].second;
            }
            else
