class Solution {
public:
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        vector<int> ans(n);
        vector<vector<int>> g(n);
        for (auto x : edges) {
			// x[0] can go to x[1]
            g[x[0]].push_back(x[1]);
			// x[1] can go to x[0]
            g[x[1]].push_back(x[0]);
        }
        // u = current node
        // p = parent node
        function<vector<int>(int, int)> dfs = [&] (int u, int p) {
            // count each label in the subtree
            vector<int> cnt(26);
            // init the current label
            cnt[labels[u] - 'a'] = 1;
            // traverse the children of u
            for (auto v : g[u]) {
                // if v is not same as p
                if (p ^ v) {
                    // get the count of labels in subtree
                    vector<int> cnt2 = dfs(v, u);
                    // add it back to cnt
                    for (int i = 0; i < 26; i++) cnt[i] += cnt2[i];
                }
            }
            // set the answer
            ans[u] = cnt[labels[u] - 'a'];
            return cnt;
        };
        dfs(0, -1);
        return ans;
    }
};
