void dfs(int node,vector<bool>&vis,vector<int>adj[],vector<int>&ans,vector<int>&freqmap,string& labels){
    vis[node] = 1;
    int prevc = freqmap[labels[node]-'a'];
    for(auto i:adj[node]){
        if(!vis[i]){
            dfs(i,vis,adj,ans,freqmap,labels);
        }
    }
    freqmap[labels[node]-'a']++;
    ans[node] = freqmap[labels[node]-'a']-prevc;
}
