class Solution {
public:
    vector<vector<int>>adj;
    vector<int>ans;
    vector<int> dfs(int node ,int par,string &lables){
        vector<int>rootC(26);
        rootC[lables[node]-'a']=1;
        for(auto x:adj[node]){
            if(x!=par){
                auto childC=dfs(x,node,lables);
                for(int i=0;i<26;i++)rootC[i]+=childC[i];
            }
        }
        ans[node]=rootC[lables[node]-'a'];
        return rootC;
    }
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string lables) {
        adj.resize(n);
        ans.resize(n);
        for(auto e:edges){
             adj[e[0]].push_back(e[1]);
             adj[e[1]].push_back(e[0]);
        }
        dfs(0,-1,lables);
        return ans;
    }
};
