impl Solution {
    pub fn count_sub_trees(n: i32, edges: Vec<Vec<i32>>, labels: String) -> Vec<i32> {
        let n = n as usize;
        let mut alph = [0; 26];
        let labels = labels
            .into_bytes()
            .iter()
            .map(|v| (*v - 97) as usize)
            .collect::<Vec<usize>>();
        let mut ans = vec![0; n];

        let mut subtrees = vec![Vec::new(); n];
        for edge in edges {
            let a = edge[0] as usize;
            let b = edge[1] as usize;
            subtrees[a].push(b);
            subtrees[b].push(a);
        }

        Self::dfs(0, n, &mut alph, &subtrees, &mut ans, &labels);

        ans
    }

    fn dfs(
        node: usize,
        parent: usize,
        alph: &mut [usize; 26],
        subtree: &Vec<Vec<usize>>,
        ans: &mut Vec<i32>,
        labels: &Vec<usize>,
    ) {
        let c = labels[node];
        let temp = alph[c];
        alph[c] = 1;

        for child in subtree[node].iter() {
            let child = *child;
            if child == parent {
                continue;
            }

            Self::dfs(child, node, alph, subtree, ans, labels);
        }

        ans[node] = alph[c] as i32;
        alph[c] += temp;
    }
}
