class Solution {
    public int[] countSubTrees(int n, int[][] edges, String labels) {
        //define parameters
        Map<Integer, List<Integer>> map = new HashMap<>();
        int[] res = new int[n];
        boolean[] visited = new boolean[n];

        //initialize
        for(int[] edge : edges) {
            if(!map.containsKey(edge[0])) map.put(edge[0], new ArrayList<>());
            if(!map.containsKey(edge[1])) map.put(edge[1], new ArrayList<>());
            map.get(edge[0]).add(edge[1]);
            map.get(edge[1]).add(edge[0]);
        }

        dfs(map, labels, res, 0, visited);

        return res;
    }

    public int[] dfs(Map<Integer, List<Integer>> map, String labels, int[] res, int start, boolean[] visited) {

        visited[start] = true;
        int[] first = new int[26];

        first[labels.charAt(start) - 'a'] ++;
        for(Integer i : map.get(start)) {
            if(!visited[i]) {
                int[] second = dfs(map, labels, res, i.intValue(), visited);
                for(int j = 0; j < 26; j ++) {
                    first[j] += second[j];
                }
            }
        }
        res[start] = first[labels.charAt(start) - 'a'];
        return first;
    }
}
