class Solution {
public:
    vector<int> fun(vector<vector<int>> &adj, string &labels, int i,vector<int>&result){
        vector<int> ans(26, 0);
        result[i] = 1;
        ans[labels[i] - 'a'] = 1;

        for(int j = 0; j != adj[i].size(); j++)
        if(!result[adj[i][j]]){
            vector<int> tmp = fun(adj, labels,adj[i][j],result);
            for(int k = 0; k != 26; k++) ans[k] += tmp[k];
        }

        result[i] = ans[labels[i] - 'a'];

        return ans;
