class Solution {
public:
    vector<int> countSubTreesH(unordered_map<int,vector<int>>& m,string& labels,vector<int>& ans,int node,vector<bool>& visited){
        visited[node]=true;
        char ch=labels[node];
        vector<int> freqLabels(26,0);
        vector<int> subTree;
        //I have to find number of descendants in all subtrees
        for(int i=0;i<m[node].size();i++){
            if(!visited[m[node][i]]){
                subTree=countSubTreesH(m,labels,ans,m[node][i],visited);
                for(int j=0;j<26;j++){
                    freqLabels[j]+=subTree[j];
                }
            }
        }
        //Adding root value also
        freqLabels[ch-'a']++;

        ans[node]=freqLabels[ch-'a'];
        visited[node]=false;
        return freqLabels;
    }
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        //I should return a frequency array consisiting of labels from all subtrees as labels have constant size
        //It will be of size 26, therefore tc will be n*26

        //Firstly I gotta construct this tree or undirected graph with no edges
        //I dont know parent child relationship edges[i][0] can be parent of edges[i][0] and vice versa also
        unordered_map<int,vector<int>> m;
        for(int i=0;i<edges.size();i++){
            m[edges[i][0]].push_back(edges[i][1]);
            m[edges[i][1]].push_back(edges[i][0]);
        }
        vector<bool> visited(n,false);
        vector<int> ans(n,0);
        countSubTreesH(m,labels,ans,0,visited);
        return ans;
    }
};

