class Solution:
    def countSubTrees(self, N: int, E: List[List[int]], A: str) -> List[int]:
        adj = defaultdict(set)
        for u, v in E:
            adj[u].add(v)
            adj[v].add(u)
        ans = [0] * N
        def go(u = 0, seen = set([0])):
            cnt = Counter()
            for v in adj[u]:
                if v in seen:
                    continue
                seen.add(v)
                cnt += go(v, seen)
            cnt[A[u]] += 1
            ans[u] = cnt[A[u]]
            return cnt
        go()
