class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    using Set = unordered_set<int>;
    using Map = unordered_map<char, int>;
    using Adj = unordered_map<int, Set>;
    using fun = function<Map(int, Set&&)>;
    VI countSubTrees(int N, VVI& E, string A, Adj adj = {}) {
        for (auto& edge: E) {
            auto [u, v] = tie(edge[0], edge[1]);
            adj[u].insert(v);
            adj[v].insert(u);
        }
        VI ans(N);
        fun go = [&](auto u, auto&& seen) {
            Map cnt;
            for (auto v: adj[u]) {
                if (!seen.insert(v).second)
                    continue;
                auto m = go(v, move(seen));
                for (auto c{ 'a' }; c <= 'z'; ++c)
                    cnt[c] += m[c];
            }
            ans[u] = ++cnt[A[u]];
            return cnt;
        };
        go(0, {0});
        return ans;
    }
};
```

