class Solution {
    public int[] countSubTrees(int n, int[][] edges, String labels) {

        Map<Integer, List<Integer>> adjList = new HashMap<>();

        for (int i = 0; i < edges.length; i++) {
            adjList.putIfAbsent(edges[i][0], new ArrayList<>());
            adjList.putIfAbsent(edges[i][1], new ArrayList<>());
            adjList.get(edges[i][0]).add(edges[i][1]);
            adjList.get(edges[i][1]).add(edges[i][0]);
        }

        int[] result = new int[n];
        Arrays.fill(result, 1);

        Map<Integer, Integer> parent = new HashMap<>();
        boolean[] visited = new boolean[n];
        int[][] auxiliaryCount = new int[n][26];

        for (int i = 0; i < n; i++) {
            char ch = labels.charAt(i);
            int idx = Character.getNumericValue(ch) - 10;
            auxiliaryCount[i][idx] = 1;
        }

        Stack<Integer> s = new Stack<>();
        s.push(0);
        parent.put(0, null);

        while (!s.isEmpty()) {
            int val = s.peek();
            visited[val] = true;

            boolean noUnvisitedNeighbour = true;

            for (int neighbour : adjList.get(val)) {
                if (!visited[neighbour]) {
                    s.push(neighbour);
                    parent.put(neighbour, val);
                    noUnvisitedNeighbour = false;
                }
            }

            if (noUnvisitedNeighbour) {
                char ch = labels.charAt(val);
                int idx = Character.getNumericValue(ch) - 10;
                result[val] = auxiliaryCount[val][idx];

                Integer p = parent.get(val);

                if (p != null) {
                    for (int i = 0; i < 26; i++) {
                        auxiliaryCount[p][i] += auxiliaryCount[val][i];
                    }
                }

                s.pop();
            }
        }

        return result;
    }
}
