            int[] chCnt = Dfs(adj,adj[root][i],labels,visited,ans);

            for(int j=0;j<26;j++)
                charCount[j]+=chCnt[j];
        }

        ans[root] = charCount[labels[root]-'a'];

        return charCount;
    }

