            int[] arr = helper(graph, ans, next, cur, labels);
            for(int i = 0; i < 26; i++) {
                cnt[i] = cnt[i] + arr[i];
            }
        }
        char c = labels.charAt(cur);
        cnt[c - 'a']++;
        ans[cur] = cnt[c - 'a'];
        return cnt;
