    void countSubTreeLabels(int node, vector<vector<int>> &adjList, vector<int> &labelCount, vector<bool> &visited, vector<int> &subTreeCount, string &labels) {
        if(visited[node]) return;
        visited[node] = true;
        int countBefore = labelCount[labels[node]-'a'];
        // cout<<node<<" "<<labels[node]<<" "<<countBefore<<endl;

        ++labelCount[labels[node]-'a'];
        for(auto adjElem : adjList[node]) {
            countSubTreeLabels(adjElem, adjList, labelCount, visited, subTreeCount, labels);
        }
        // cout<<"after "<<node<<" "<<labels[node]<<" "<<labelCount[labels[node]-'a']<<endl;
        subTreeCount[node] = labelCount[labels[node]-'a'] - countBefore;
    }
};
