			adj[u].push_back(v);
			adj[v].push_back(u);
		}

		vector<int> vis(n, -1);
		vector<int> cnt(26, 0);
		vector<int> ans(n);
		cnt = dfs(0, adj, vis, str, ans);
		return ans;
	}
};
