class Solution {
    vector<int>ans;
    vector<vector<int>>adj;
    vector<bool>vis;

public:
    vector<int> solve(string &l,int node,int par)
    {

        vector<int>temp(26,0);vis[node]=true;
        temp[l[node]-'a']+=1;
        for(auto it:adj[node])
        {
            if(!vis[it])
            {
               vector<int>nv= solve(l,it,node);
               for(int i=0;i<26;++i)temp[i]+=nv[i];
            }
        }

        ans[node]=temp[l[node]-'a'];
        return temp;
    }
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        ans.resize(n,0);
        adj.resize(n);
        vis.resize(n,false);
        int a,b;
        for(auto it:edges)
        {
            a=it[0];b=it[1];
            adj[a].push_back(b);
            adj[b].push_back(a);
        }

        solve(labels,0,-1);
        return ans;
    }
};
```

