class Solution {
    int[] res;
    boolean[] seen;
    HashMap<Integer, ArrayList<Integer>> graph;

    int[] dfs(int index, String labels){
        int[] hash = new int[26];
        if(!graph.containsKey(index)) return hash;
        seen[index] = true;

        for(int i : graph.get(index)){
            if(!seen[i]){
                int[] temp = dfs(i, labels);
                for(int j = 0; j < 26; j++) hash[j] += temp[j];
            }
        }

        hash[labels.charAt(index) - 'a']++;
        res[index] = hash[labels.charAt(index) - 'a'];
        return hash;
    }

    public int[] countSubTrees(int n, int[][] edges, String labels) {
        res = new int[n];
        graph = new HashMap<>();
        seen = new boolean[n];
        for(int i = 0; i < edges.length; i++){
            if(!graph.containsKey(edges[i][0])) graph.put(edges[i][0], new ArrayList<>());
            if(!graph.containsKey(edges[i][1])) graph.put(edges[i][1], new ArrayList<>());
            graph.get(edges[i][0]).add(edges[i][1]);
            graph.get(edges[i][1]).add(edges[i][0]);
        }

        dfs(0, labels);
        return res;
    }
}
