class Solution {
public:
    vector<unordered_set<int>>childs;
    string labelofnode;
    vector<int>ans;
    vector<int>Labelvalues(int x)
    {
        vector<int>labelvals(26,0);
        if(childs[x].size()==0)
        {
            labelvals[labelofnode[x]-'a']++;
            ans[x]=labelvals[labelofnode[x]-'a'];
            return labelvals;
        }
        for(auto j:childs[x])
        {
            childs[j].erase(x);
            vector<int>increment=Labelvalues(j);
            for(int i=0;i<26;i++)
            {
                labelvals[i]+=increment[i];
            }
        }
        labelvals[labelofnode[x]-'a']++;
        ans[x]=labelvals[labelofnode[x]-'a'];
        return labelvals;
    }
    vector<int> countSubTrees(int n,vector<vector<int>>& edges,string labels)
    {
        childs.assign(n,{});
        ans.assign(n,0);
        labelofnode=labels;
        for(int i=0;i<edges.size();i++)
        {
            childs[edges[i][0]].insert(edges[i][1]);
            childs[edges[i][1]].insert(edges[i][0]);
        }
        vector<int>labelvals(26,0);
        for(auto j:childs[0])
        {
            childs[j].erase(0);
            vector<int>increment=Labelvalues(j);
            for(int i=0;i<26;i++)
            {
                labelvals[i]+=increment[i];
            }
        }
        labelvals[labelofnode[0]-'a']++;
