class Solution {
public:
    vector<vector<int>> vertex, freq;

    void dfs(int curr, int prev, string& labels, vector<int>& result) {
        for (int neigh : vertex[curr]) {
            if (neigh != prev) {
                dfs(neigh, curr, labels, result);
                for (int i = 0; i < 26; i++) { freq[curr][i] += freq[neigh][i]; }
            }
        }
        result[curr] = ++freq[curr][labels[curr]-'a'];
    }

    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string& labels) {
        vertex.resize(n);
        for (int i = 0; i < edges.size(); i++) {
            int a = edges[i][0], b = edges[i][1];
            vertex[a].push_back(b);
            vertex[b].push_back(a);
        }
        freq = vector<vector<int>>(n, vector<int>(26, 0));
        vector<int> result(n, 0);
        dfs(0, -1, labels, result);
        return result;
    }
};
