class Solution {
    int res[];
    char[] label;
    public int[] countSubTrees(int n, int[][] edges, String labels) {
        res = new int[n];
        label = labels.toCharArray();
        List al[] = new List[n];
        for(int i = 0; i<n; i++) al[i] = new ArrayList<>();
        for(int edge[]: edges) {
            al[edge[0]].add(edge[1]);
            al[edge[1]].add(edge[0]);
        }
        recurse(al, -1, 0);
        return res;
    }

    private int[] recurse(List<Integer> al[], int parent, int currNode) {
        int[] currMap = new int[26];
        currMap[label[currNode]-'a'] = 1;
        for(int child: al[currNode]) {
            if(child!=parent) {
                int map[] = recurse(al, currNode, child);
                for(int i = 0; i<26; i++)
                    currMap[i] += map[i];
            }
        }
        res[currNode] = currMap[label[currNode]-'a'];
        return currMap;
    }
}
