class Solution {
    public int[] countSubTrees(int n, int[][] edges, String labels) {
        if (n == 1) return new int[]{1};
        NTreeNode[] arr = new NTreeNode[n];
        for (int i = 0; i < n; i++) {
            arr[i] = new NTreeNode(labels.charAt(i), i);
        }
        Set<Integer> hasParent = new HashSet<>();
        hasParent.add(0);
        //I create the graph
        for (int i = 0; i < edges.length; i++) {
            int a = edges[i][0], b = edges[i][1];
            NTreeNode aNode = arr[a], bNode = arr[b];
            if (hasParent.contains(a)) {
                aNode.children.add(bNode);
                hasParent.add(b);
            } else {
                bNode.children.add(aNode);
                hasParent.add(a);
            }
        }

        Map<Character, List<NTreeNode>> parents = new HashMap<>();
        int[] freq = new int[n];
        //traversing the graph and incrementing parents' frequency
        //when I meet a child with the same label
        traverseGraphDFS(arr[0], parents);
        for (int i = 0; i < arr.length; i++) {
            freq[i] = arr[i].freq;
        }
        return freq;
    }

    private void traverseGraphDFS(NTreeNode node, Map<Character, List<NTreeNode>> parents) {
        if (node == null) return;

        node.freq++;
        List<NTreeNode> list = parents.get(node.val);
        if (list != null) {
            for (NTreeNode parent : list) {
                parent.freq++;
            }
            list.add(node);
        } else {
            list = new ArrayList<>();
            list.add(node);
            parents.put(node.val, list);
        }

        for (NTreeNode curr : node.children) {
            traverseGraphDFS(curr, parents);
        }
        list.remove(list.size() - 1);
    }

    class NTreeNode {
        public char val;
        public List<NTreeNode> children;
        public int freq;
        int index;

        public NTreeNode() {}

        public NTreeNode(char _val, int idx) {
            val = _val;
            index = idx;
            children = new ArrayList<>();
        }

        public NTreeNode(char _val, List<NTreeNode> _children) {
            val = _val;
            children = _children;
        }
    }
}
