class Solution {
    fun countSubTrees(n: Int, edges: Array<IntArray>, labels: String): IntArray {
        val graph = ArrayList<ArrayList<Int>>()
        for(i in 0 until n) {
            graph.add(ArrayList<Int>())
        }

        for(edge in edges) {
            graph[edge[0]].add(edge[1])
            graph[edge[1]].add(edge[0])
        }

        val visited: BooleanArray = BooleanArray(n){false}
        val ans: IntArray = IntArray(n){0}
        dfs(0, graph, ans, visited, labels)
        return ans
    }

    fun dfs(currNode: Int, graph: ArrayList<ArrayList<Int>>, ans: IntArray, visited: BooleanArray, labels: String): IntArray {
        val cnt: IntArray = IntArray(26){0}
        if(visited[currNode]) {
            return cnt
        }

        visited[currNode] = true
        val currChart: Char = labels[currNode]

        for(i in 0 until graph[currNode].size) {
            val sub: IntArray = dfs(graph[currNode][i], graph, ans, visited, labels)
            for(j in 0 until 26) {
                cnt[j] += sub[j]
            }
        }

        cnt[currChart-'a']++
        ans[currNode] = cnt[currChart-'a']
        return cnt
    }
}
